import { useLocation,Link } from "react-router-dom"
import "./singlePost.css"
import { useContext, useEffect ,useState} from "react"


import axios from "axios"
import { Context } from "../../context/Context"
import { Z_ASCII } from "zlib"

export default function SinglePost() {
  const location =useLocation()
  const path=location.pathname.split("/")[2]
  const [post,setPost]=useState({})
  const PF="localhost:5000/images/"
  const {user}=useContext(Context);

  const x=post.username;
  console.log(path);
  const z=post._id;

  useEffect(()=>{
    const getPost=async()=>{
       const res=await axios.get("/post/"+path)
      setPost(res.data)
    };
    getPost();
  },[path])

// const handleDelete=async()=>{
//     try{
//       console.log("delete begun");
//       await axios.delete(`/posts/${post._id}`,{
//         data:{username:user.username}});
//       window.location.replace("/");
//       console.log("calling delete here 22");

//     }catch(err)
//     {
//       console.log("some error while deleting");
//     }
//   }
const handleDelete = async () => {
  try {
    console.log("hand delete loki occhinam")
    await axios.delete('/post/'+z, {
       data: { username: user.username },

    });
    console.log("hand delete lo function call aindhi")
    window.location.replace("/");
  } catch (err) {
    console.log("ERROR IN FRONTEND");
  }
};



  console.log(post.username===user.username);
  return (
    <div className="singlePost">
    <div className="singlePostWrapper">
      {post.photo &&(<img
        className="singlePostImg"
        src={PF+post.photo}//here also made changes regarding photos
        alt=""
      />)}

      <h1 className="singlePostTitle">
       {post.title}
       {
        post.username===user?.username&&(
        <div className="singlePostEdit">
          <i className="singlePostIcon far fa-edit"></i>
          <i className="singlePostIcon far fa-trash-alt" onClick={handleDelete}></i>
        </div>
        )}
      </h1>
      <div className="singlePostInfo">
        <span>
          Author:
          <b className="singlePostAuthor">

            <Link className="link"to={'/?user='+x}>{post.username}</Link>

          </b>
        </span>
        <span>{new Date(post.createdAt).toDateString()}</span>
      </div>
      <p className="singlePostDesc">
      {post.desc}
      </p>
    </div>
  </div>
  )
}
