import { useLocation,Link } from "react-router-dom"
import "./singlePost.css"
import { useContext, useEffect ,useState} from "react"


import axios from "axios"
import { Context } from "../../context/Context"

export default function SinglePost() {
  const location =useLocation()
  const path=location.pathname.split("/")[2]
  const [post,setPost]=useState({})
  const PF="localhost:5000/images/"
  const {user}=useContext(Context);



  const x=post.username;
  console.log(path);

  useEffect(()=>{
    const getPost=async()=>{
       const res=await axios.get("/post/"+path)
      setPost(res.data)
    };
    getPost();
  },[path])

  const handleDelete=async()=>{
    try{
      await axios.delete("post/"+path);
    }catch(err)
    {
      console.log("some error while deleting");
    }
  }

  return (
    <div className="singlePost">
    <div className="singlePostWrapper">
      {post.photo &&(<img
        className="singlePostImg"
        src={PF+post.photo}//here also made changes regarding photos
        alt=""
      />)}

      <h1 className="singlePostTitle">
       {post.title}
       {
        post.username===user?.username&&(
        <div className="singlePostEdit">
          <i className="singlePostIcon far fa-edit"></i>
          <i className="singlePostIcon far fa-trash-alt" onClick={handleDelete}></i>
        </div>
        )}
      </h1>
      <div className="singlePostInfo">
        <span>
          Author:
          <b className="singlePostAuthor">

            <Link className="link"to={'/?user='+x}>{post.username}</Link>

          </b>
        </span>
        <span>{new Date(post.createdAt).toDateString()}</span>
      </div>
      <p className="singlePostDesc">
      {post.desc}
      </p>
    </div>
  </div>
  )
}
