import "./settings.css";
import Sidebar from "../../components/sidebar/Sidebar";
import { Context } from "../../context/Context";
import { useContext, useState } from "react";
import axios from "axios";


export default function Settings() {
  const {user}=useContext(Context)
  const [file,setFile]=useState(null);
  const [username,setUsername]=useState("");
  const [email,setEmail]=useState("");
  const [password,setPassword]=useState("");
  const [success,setSuccess]=useState("");

  const handleSubmit= async(e)=>{
    // console.log("handle submit is on");
    e.preventDefault();
    const updateUser={
      userId: user._id,
     username,email,password
    }
    if(file){
      const data= new FormData();
      const filename =Date.now()+file.name;
      data.append("name",filename)
      data.append("file",file);
      updateUser.profilePic=filename;
      try
      {
          await axios.post("/upload",data);
      }
      catch(err)
      {
        console.log("error at images");
      }
    }
    try{
     await axios.put("/user/"+user._id,updateUser);
     setSuccess(true);

    }catch(err)
    {
      console.log("ERROR AT UPDATE 2")
    }
  }

  return (
    <div className="settings">
      <div className="settingsWrapper">
        <div className="settingsTitle">
          <span className="settingsTitleUpdate">Update Your Account</span>
          <span className="settingsTitleDelete">Delete Account</span>
        </div>
        <form className="settingsForm" onSubmit={handleSubmit}>
          <label>Profile Picture</label>
          <div className="settingsPP">
            <img
              src={user.profilePic}
              alt=""
            />
            <label htmlFor="fileInput">
              <i className="settingsPPIcon far fa-user-circle"></i>{" "}
            </label>
            <input
              id="fileInput"
              type="file"
              style={{ display: "none" }}
              className="settingsPPInput"
              onChange={(e)=>setFile(e.target.files[0])}
            />
          </div>
          <label>Username</label>
          <input type="text" placeholder={user.username} name="name" onChange={(e)=>setUsername(e.target.value)}/>
          <label>Email</label>
          <input type="email" placeholder={user.email} name="email" onChange={(e)=>setEmail(e.target.value)} />
          <label>Password</label>
          <input type="password" placeholder="Password" name="password" onChange={(e)=>setPassword(e.target.value)}/>
          <button className="settingsSubmitButton" type="submit">
            Update
          </button>
          {success&&<span style={{color:"green"}}>Profile has been Updated....</span>}
        </form>
      </div>
      <Sidebar />
    </div>
  );
}
